const apiKey = 'LL4MTSCljL47U8LeX5dYRByVuwAoIefo';

/*Esa sintaxis de prop es: Si no le mandamos nada desde la llamada kayword = 'panda,
y si ni si quiera declaramos keyword en la llamada, que ademas este preparado para
recibir un prop vacio, entonces son dos valores por defecto al mismo tiempo. */
export default function getGifs( {keyword = 'panda'} =  {} ) {

  const apiURL = `https://api.giphy.com/v1/gifs/search?api_key=${apiKey}&q=${keyword}&limit=25&offset=0&rating=g&lang=en`;

  return fetch(apiURL)
          .then(res => res.json())
          .then(response => {

          /*Estos datos lo sacamos yendo a la url. Entonces como en este caso quiero 
          obtener las imagenes accedemos a data->images->downsized_medium->url, y 
          ahi con map retornamos todos las url. */
          const {data = []} = response;
          if(Array.isArray(data)){
            const gifs = data.map(image => image.images.downsized_medium.url)
            return gifs;
          }
        })
}
