import React, {useEffect, useState} from 'react';
import './App.css';

const apiURL = 'https://api.giphy.com/v1/gifs/search?api_key=LL4MTSCljL47U8LeX5dYRByVuwAoIefo&q=pand&limit=25&offset=0&rating=g&lang=en';

function App() {

  const [gifs, setGifs] = useState([])

  //Este HOOK nos permite ejecutar una funcion cada vez que se renderice nuestro componente.
  useEffect(function() {
    fetch(apiURL)
      .then(res => res.json())
      .then(response => {
        const {data} = response;
        const gifs = data.map(image)
      })
  }, [])  //Los corchetes estan para que esta funcion solo se ejecute la primera vez, si no los ponemos, la funcion entraria en un bucle infinito ya que la funcion se ejecutaria cada vez que se renderice el componente, y como estamos llamando al setGifs y actualizando el estado, esto se haria infinitamente.

  return (
    <div className="App">
      <section className="App-content">
        {
          gifs.map(singleGif => <img src={singleGif} />)
        }
      </section>
    </div>
  );
}

export default App;
