import React, { useState } from "react";
import { Link, useLocation} from "wouter";
import { useGifs } from '../../hooks/useGifs';
import Spinner from 'react-bootstrap/Spinner';
import ListOfGifs from '../../componentes/ListOfGifs/ListOfGifs.js';
import TrendingSearches from "../../componentes/TrendingSearches/TrendingSearches";
import SearchForm from "../../componentes/SearchForm/SearchForm";
import './index.css';

export default function Home(){
  
  const {loading, gifs} = useGifs();
  const [keyword, setKeyword] = useState('')

  /*Este hook devuelve un array de dos posiciones: en la primera pos tendriamos el 
  path en donde estamos, y en la segunda posicion le pasamos la navegacion para ir. */
  const pushLocation = useLocation();

  const handleSubmit = event => {
    event.preventDefault();
    pushLocation(`/search/${keyword}`); //Navegamos a otra ruta
  }

  const handleChange = event => {
    setKeyword(event.target.value); 
  }
  
  return (
    <>
      <SearchForm handleChange handleSubmit />
      <div className="Container">
        {loading ? (
          <Spinner animation="border" variant="info" >
            <span className="visually-hidden">Loading...</span>
          </Spinner>
          ) : (
            <ListOfGifs gifs={gifs} />
          )
        } 
        <div className="App-category">
          <TrendingSearches />
        </div>
      </div>
    </>
  )
}
