import React from 'react';
import logo from './logo.svg'
import './styles/App.css';
import { Link, Route } from 'wouter';
import Home from './pages/Home';
import SearchResult from './pages/SearchResult';
import Detail from './pages/Detail';
import StaticContext from './context/StaticContext';
import { GifsContexProvider } from './context/GifsContext';

function App() {

  return ( 
    // Con esto le damos la posibilidad a toda la app de usar este proovedor. 
    <StaticContext.Provider value={{name: 'canor', soyUnaMaquina: true}}>
      <div className="App"> 
        <Link to='/' >
          <a><img className='logo' src={logo} /> </a>
        </Link>

          <Route 
            component={Home}
            path='/' />
          <Route 
            component={SearchResult}
            path='/search/:keyword'  />
          <Route 
            component={Detail}
            path='/gif/:id'  />

      </div> 
    </StaticContext.Provider>
  )
}

export default App;
